INSERT INTO `pages` VALUES (1,'Giới thiệu','Gioi thieu','Đá Marble và Granite là loại đá được ứng dụng rộng rãi trong ốp lát ngành xây dựng. Với Ưu điểm của 2 loại vật liệu này là tạo phong cách lịch lãm, thoáng mát, cách nhiệt mang đến cho chủ nhân căn nhà một sự hòa hợp tuyệt vời giữa phong cách cổ điển và hiện đại. Ngoài ra, đá marble – Granite còn có tính chịu lực cao và độ bền tốt. Độ dày của đá cho phép nền nhà chịu được sự va đập mạnh.','<p>Đ&aacute; Marble v&agrave; Granite l&agrave; loại đ&aacute; được ứng dụng rộng r&atilde;i trong ốp l&aacute;t ng&agrave;nh x&acirc;y dựng. Với Ưu điểm của 2 loại vật liệu n&agrave;y l&agrave; tạo phong c&aacute;ch lịch l&atilde;m, tho&aacute;ng m&aacute;t, c&aacute;ch nhiệt mang đến cho chủ nh&acirc;n căn nh&agrave; một sự h&ograve;a hợp tuyệt vời giữa phong c&aacute;ch cổ điển v&agrave; hiện đại. Ngo&agrave;i ra, đ&aacute; marble &ndash; Granite c&ograve;n c&oacute; t&iacute;nh chịu lực cao v&agrave; độ bền tốt. Độ d&agrave;y của đ&aacute; cho ph&eacute;p nền nh&agrave; chịu được sự va đập mạnh.</p>\r\n','2016/09/20/30062159-1474369497.png','gioi-thieu',1,'Giới thiệu','Giới thiệu','Giới thiệu','Giới thiệu',0,0,'2016-09-20 11:04:32','2016-09-20 11:04:58');
