<script type="text/javascript" src="http://maps.google.com/maps/api/js?sensor=true"></script>
   <script type="text/javascript">
   var map;
   var infowindow;
   var marker= new Array();
   var old_id= 0;
   
   var infoWindowArray= new Array();
   var infowindow_array= new Array();
   function initialize(){
       var defaultLatLng = new google.maps.LatLng(10.79205,106.6952273);
       var myOptions= {
           zoom: 16,
           center: defaultLatLng,
           scrollwheel : false,
           mapTypeId: google.maps.MapTypeId.ROADMAP
        };
        map = new google.maps.Map(document.getElementById("map_canvas"), myOptions);map.setCenter(defaultLatLng);
        
                var arrLatLng = new google.maps.LatLng(10.79205,106.6952273);
            infoWindowArray[0+1] = "<div class='map_description'><div class='map_title'>Tên: Trụ Sở Giao Dịch<br/> Địa Chỉ: 27 Nguyễn Văn Giải, P. Đakao, Quận 1, TP. HCM<br/> Điện thoại : 0933 080 889</div><div>";
            loadMarker(arrLatLng, infoWindowArray[0+1], 0+1);
    
        
                var arrLatLng = new google.maps.LatLng(10.863155,106.7178618);
            infoWindowArray[1+1] = "<div class='map_description'><div class='map_title'>Tên: Kho Xưởng Công Ty<br/> Địa Chỉ: 2555 Quốc Lộ 1, P Tân Hưng Thuận, Quận 12, TP. HCM<br/> Điện thoại : 0933 080 889</div><div>";
            loadMarker(arrLatLng, infoWindowArray[1+1], 1+1);
    
                moveToMaker(1,'lol0');
       }
       
       function loadMarker(myLocation, myInfoWindow, id){
           marker[id] = new google.maps.Marker({position: myLocation, map: map, visible:true});
       var popup = myInfoWindow;
       infowindow_array[id] = new google.maps.InfoWindow({ content: popup});
        google.maps.event.addListener(marker[id], 'click', function() {
            map.setZoom(12);
            map.setCenter(marker[id].getPosition());
        });
       google.maps.event.addListener(marker[id], 'mouseover', function() {if (id == old_id) return;if (old_id > 0) infowindow_array[old_id].close();infowindow_array[id].open(map, marker[id]);old_id = id;});google.maps.event.addListener(infowindow_array[id], 'closeclick', function() {old_id = 0;});}
       function moveToMaker(id,ob){
          var location = marker[id].position;map.setCenter(location);
          if (old_id > 0) infowindow_array[old_id].close();
          infowindow_array[id].open(map, marker[id]);
          old_id = id;

          $("#list_location").find('li').removeClass('active');
          $("#"+ob).addClass('active');
       }
       
       function resetmap(){
           map.setZoom(6);
           map.setCenter(marker[1].getPosition());
       }
      </script>
